$version = false;

$h = opendir('./');
$re = '/cms4vr-(\d+\.\d+\.\d+).*\.zip/';

while (false !== ($entry = readdir($h))) {
    preg_match($re, $entry, $matches);
    if (!empty($matches)) {
        $version = $matches[1];
    }
}

if (!$version) {
    echo 'Installer package not found! Exiting.';
    exit;
}

$file = 'cms4vr-' . $version . '.zip';

function returnJson($data) {
    header('Content-type: application/json');
    echo json_encode($data);
    exit;
}

if (isset($_GET['check'])) {

    switch ($_GET['mode']) {
        case 'exec':
            $zip = new ZipArchive();
            $zip->open($file);
            $zip->extractTo('../', 'admin/data/bin/krpano/krpanotools');
            $zip->close();
            shell_exec('chmod +x ' . __DIR__ . '/../admin/data/bin/krpano/krpanotools');
            $shellExec = trim(shell_exec(__DIR__ . '/../admin/data/bin/krpano/krpanotools')) != '' ? true : false;
            returnJson(['shellExec' => $shellExec]);
            break;
        case 'ffmpeg':
            $ffmpeg = false;
            $cmd = strtolower(substr(PHP_OS, 0, 3)) === 'win' ? 'where' : 'which';
            if (`$cmd ffmpeg`) {
                $ffmpeg = true;
            }
            returnJson(['ffmpeg' => $ffmpeg]);
            break;
        case 'writable':
            $writable = false;
            if (is_writable('../../')) {
                $writable = true;
            }
            returnJson(['writable' => $writable]);
            break;
        case 'license':
            $url = 'https://cms4vr.com/licenses/api.php?a=license';
            $ch = curl_init($url);

            curl_setopt($ch, CURLOPT_POST, 1);
            curl_setopt($ch, CURLOPT_POSTFIELDS, ['license' => $_POST['license']]);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);

            $response = curl_exec($ch);
            curl_close($ch);

            $licenseResponse = json_decode($response);

            $version = 'banned';

            if (isset($licenseResponse->success) && $licenseResponse->success) {
                $version = $licenseResponse->version;
            }

            returnJson(['success' => true, 'version' => $version]);
            break;
    }
    exit;
}

if (isset($_GET['unpack'])) {

    try {
        $zip = new ZipArchive();
        $zip->open($file);
        $zip->extractTo('../');
        $zip->close();

        rename(__DIR__ . '/../admin/data/vrm-default.db', __DIR__ . '/../admin/data/vrm.db');

        $db = new SQLite3(__DIR__ . '/../admin/data/vrm.db');

        $db->exec('INSERT INTO settings(`id`, `key`, `value`) VALUES(NULL, "default_language", "en")');

        $db->exec('INSERT INTO settings(`id`, `key`, `value`) VALUES(NULL, "ffmpeg_enabled", "' . (int)$_POST['ffmpeg'] . '")');

        $db->exec('INSERT INTO settings(`id`, `key`, `value`) VALUES(NULL, "shellExec_enabled", "' . (int)$_POST['shellExec'] . '")');

        $db->exec('INSERT INTO settings(`id`, `key`, `value`) VALUES(NULL, "writable_enabled", "' . (int)$_POST['writable'] . '")');

        $db->exec('INSERT INTO settings(`id`, `key`, `value`) VALUES(NULL, "template", "rubee")');

        $globalModules = '{"m_map":{"aktyw":"","maptype":"normal","distance":"kilometers","id":""},"m_facebook":{"aktyw":"","id":"","app_id":""},"m_analytics":{"aktyw":"","id":""},"m_logo":{"aktyw":"","id":"","www":""},"m_copyright":{"aktyw":"","id":"","www":""},"m_social":{"aktyw":"true"},"m_stickers":{"aktyw":"","groups":""},"m_infobox":{"aktyw":""},"m_keepview":{"aktyw":""},"m_arrowmode":{"aktyw":""},"m_radarmode":{"aktyw":""},"m_floorplans":{"aktyw":""},"m_autotour":{"aktyw":"false","speed":10},"m_simple_content_menu":{"aktyw":"false"},"m_poi_settings":{"poi_size":28,"poi_css_size":18,"poi_flip_color":"false","poi_hide_popup_to_pano_vr":"false","ico_start_rotate":45,"bg_ico_start_rotate":-45,"poi_roundedge":6}}';

        $db->exec('INSERT INTO settings(`id`, `key`, `value`) VALUES(NULL, "global_modules", \'' . $globalModules . '\')');

        $langSettings = '{"en":{"name":"' . str_replace(["'", '"'], ["''", '\"'], filter_var($_POST['project_name'])) . '","description":""}}';

        $db->exec('INSERT INTO settings(`id`, `key`, `value`) VALUES(NULL, "project_lang_settings", \'' . $langSettings . '\')');

        $db->exec('INSERT INTO settings(`id`, `key`, `value`) VALUES(NULL, "license", \'' . $_POST['cms4vr_license'] . '\')');

        $db->exec('INSERT INTO settings(`id`, `key`, `value`) VALUES(NULL, "cms_dev_version", \'' . $_POST['cms_dev_version'] . '\')');

        $db->exec('INSERT INTO settings(`id`, `key`, `value`) VALUES(NULL, "version", \'' . $version . '\')');

        $displayName = str_replace('"', '""', filter_var($_POST['display_name']));
        $login = str_replace('"', '""', filter_var($_POST['login']));
        $password = filter_var($_POST['password']);
        $passwordHash = password_hash($password,PASSWORD_BCRYPT);
        $db->exec('INSERT INTO users(`id`, `display_name`, `login`, `password`, `date_added`, `active`, `usertype`) VALUES(NULL, "' . $displayName . '", "' . $login . '", "' . $passwordHash . '", "' . date('Y-m-d H:i:s') . '", "1", "developer")');

        // save a copy of init database for future reset option
        copy(__DIR__ . '/../admin/data/vrm.db', __DIR__ . '/../admin/data/vrm-init.db');

        // register krpano
        $regCode = filter_var(str_replace([PHP_EOL, "\n", "\r"], '', $_POST['krpano_license']), FILTER_SANITIZE_STRING);

        if (isset($_POST['krpano_save']) && $_POST['krpano_save'] == 1 && $regCode !== '') {
            $db->exec('INSERT INTO settings(`id`, `key`, `value`) VALUES(NULL, "krpano_license", \'' . $regCode . '\')');
        }

        $db->close();

        shell_exec('chmod +x ' . __DIR__ . '/../admin/data/bin/krpano/krpanotools');

        shell_exec(__DIR__ . '/../admin/data/bin/krpano/krpanotools register ' . $regCode);

        $installDir = __DIR__ . DIRECTORY_SEPARATOR;
        @unlink($installDir . 'install.php');
        @unlink($installDir . 'index.php');
        @unlink($installDir . $file);
        @rmdir($installDir);

    } catch (Exception $e) {
        // handle errors
    }

    returnJson(['success' => true]);

    exit;
}

