            success: function(data) {
                if (data.shellExec) {
                    $('#systemChecks p[data-command="exec"] i').removeClass('fa-question-circle').addClass('fa-check-circle').attr('style', 'color:#65BD77');
                    shellExec = true;
                } else {
                    $('#systemChecks p[data-command="exec"] i').removeClass('fa-question-circle').addClass('fa-exclamation-circle').attr('style', 'color:#cd4139');
                }
                $('#myWizard input[name="shellExec"]').val(shellExec ? 1 : 0);
            }
        });
        $.ajax({
            type     : "POST",
            url      : "?check&mode=ffmpeg",
            success: function(data) {
                if (data.ffmpeg) {
                    $('#systemChecks p[data-command="ffmpeg"] i').removeClass('fa-question-circle').addClass('fa-check-circle').attr('style', 'color:#65BD77');
                    ffmpeg = true;
                } else {
                    $('#systemChecks p[data-command="ffmpeg"] i').removeClass('fa-question-circle').addClass('fa-exclamation-circle').attr('style', 'color:#cd4139');
                }
                $('#myWizard input[name="ffmpeg"]').val(ffmpeg ? 1 : 0);
            }
        });
    }
    function checkLicense() {
        $.ajax({
            type     : "POST",
            url      : "?check&mode=license",
            data     : {
                license: $('#cms4vr').val()
            },
            success: function(data) {
                if (data.success) {
                    if (data.version !== 'banned') {
                        $('input[name="cms_dev_version"]').val(data.version);
                        systemChecks();
                        $('#myWizard').easyWizard('goToStep', 2);
                    } else {
                        alert('License key not found!')
                    }
                }
            }
        });
    }
    $(document).ready(function(){

        $('input[name="cms_dev_version"]').val('');

        function checkField(field) {
            if (!$.trim($(field).val())) {
                $(field).parent().find('.error').first().show(200);
                return false;
            }
            return true;
        }

        $('#myWizard').easyWizard({
            buttonsClass: 'btn btn-default',
            submitButtonClass: 'btn btn-primary',
            submitButtonText: 'Finish',
            before: function(wizardObj, currentStepObj, nextStepObj) {
                var step = parseInt($(nextStepObj).attr('data-step'));
                if (step === 2) {
                    $('.error').hide(200);
                    if (!checkField($('#projectname')) || !checkField($('#login')) || !checkField($('#name')) || !checkField($('#password')) || !checkField($('#cms4vr'))) {
                        return false;
                    }
                    if ($.trim($('#password').val()).length < 6) {
                        $('#password').parent().find('.error-password').show(200);
                        return false;
                    }
                    var re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
                    if (!re.test($('#login').val())) {
                        $('#login').parent().find('.error').first().show(200);
                        return false;
                    }
                    if (!$('input[name="cms_dev_version"]').val()) {
                        checkLicense();
                        return false;
                    }
                }
                if (step === 4) {
                    $('div.easyWizardButtons').children()[0].remove();
                    $.ajax({
                        type     : "POST",
                        url      : "?unpack",
                        data     : $('#myWizard').serialize(),
                        success: function(data) {
                            $('#unpacking').hide(200);
                            $('#unpackingDone').show(200);
                        }
                    });

                }
            },
            after: function(wizardObj, prevStepObj, currentStepObj) {
                var step = parseInt($(currentStepObj).attr('data-step'));
                if (step === 3) {
                    $('#vrm-projectname').text($('#projectname').val());
                    $('#vrm-login').text($('#login').val());
                    $('#vrm-name').text($('#name').val());
                    $('#vrm-email').text($('#email').val());
                    $('#vrm-password').text($('#password').val());
                    if ($('#krpano').val()) {
                        $('#vrm-krpano').text('LICENSE CODE PROVIDED');
                    } else {
                        $('#vrm-krpano').text('LICENSE CODE NOT PROVIDED');
                    }

                    $('#vrm-system').html($('#systemChecks').html());
                }
            }
        });

        $('button[type="submit"]').click(function(e){
            e.preventDefault();
            window.location.href = '../admin';
        });

        $(window).resize(function() {
            $('#myWizard').easyWizard('resize', $('#myWizard').find('.active'))
        });
    });
</script>

</body>
</html>
